import { StatusBar } from 'expo-status-bar';
import React, { useState, useEffect } from 'react';
import { StyleSheet, Text, View, ActivityIndicator, Image } from 'react-native';
import MealAppService from './services/MealAppService';
import MealPrepBox from './containers/MealPrepBox';
import MyHeader from './components/MyHeader';
// import { createStackNavigator } from '@react-navigator/stack';

// const Stack = createStackNavigator();

export default function App() {

  const [items, setItems] = useState([]);


  useEffect(() => {
    MealAppService.getFoodItem()
        .then(items => setItems(items))
  }, []);

  useEffect(() => {
    console.log("list of items: ", items);
  }, [items])

  const foodItems = items.map((item, key) => {
    return <View key={key} style={styles.item}>
    <Text> {item.name} </Text>
    <Image style={{width: 150, height: 150}} source={{uri: item.imageUrl}} />
    </View>
  }) 

  return (
    <>

    <StatusBar style="auto" />
    <MealPrepBox foodItems={foodItems}></MealPrepBox>

    </>
  );
} 

const styles = StyleSheet.create({
  container: {
    flex: 0.5,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  item: {
    flex: 0.5,
    width: '100%',
    height: '100%',
    fontWeight: 'bold',
    alignSelf: 'stretch',
    flexDirection: 'row',
    margin: 1,
    alignItems: 'center',
    justifyContent: 'center',
    borderBottomWidth: 1, 
    borderBottomColor: '#eee'
  }
});
