                </View>
            </View>
            <View>
                {foodItems.name}
            </View>

        </View>
    );
}


const styles = StyleSheet.create({
    background: {
        flex: 1, // sets the image background to entire screen
        justifyContent: "space-between",
        alignItems: "center"
    },
    header: {
        width: "100%", 
        height: 135,
