import colors from '../config/colors';

export const splashScreenStyles = StyleSheet.create({
    closeIcon: {
        width: 50, 
        height: 50,
        backgroundColor: colors.grassgreen,
        position: "absolute",
        top: 40,
        left: 30
    },
    deleteIcon: {
        width: 50, 
        height: 50,
        backgroundColor: colors.grassgreen,
        position: "absolute",
        top: 40,
        right: 30
    },
    container: {
        backgroundColor: colors.black,
        flex: 1,
        alignItems: "center"
    },
    image: {
        width: "90%",
        height: "100%",
    },
})
