    //     )
    // }

    return (
        <SafeAreaView style={styles.container}>
            {renderHeader()}
            {renderCompaniesList()}
        </SafeAreaView>
    )
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: COLORS.lightGray4
    },
    shadow: {
